#!/bin/bash
# shellcheck disable=SC2034
ESCAPE_CHAR="\033"

COLOR_CLEAR=${ESCAPE_CHAR}[0m

COLOR_DEFAULT=${ESCAPE_CHAR}[39m
COLOR_BLACK=${ESCAPE_CHAR}[30m
COLOR_RED=${ESCAPE_CHAR}[31m
COLOR_GREEN=${ESCAPE_CHAR}[32m
COLOR_YELLOW=${ESCAPE_CHAR}[33m
COLOR_BLUE=${ESCAPE_CHAR}[34m
COLOR_MAGENTA=${ESCAPE_CHAR}[35m
COLOR_CYAN=${ESCAPE_CHAR}[36m
COLOR_LIGHT_GRAY=${ESCAPE_CHAR}[37m

COLOR_DIM=${ESCAPE_CHAR}[2m

# Default config file
macup_install_package="all"
